import React, { Component } from 'react'
import Navigation from './components/navigation';
import Header from './components/header';
import Features from './components/features';
import About from './components/about';
import Services from './components/services';
import Gallery from './components/gallery';
import Testimonials from './components/testimonials';
import Team from './components/Team';
import Contact from './components/contact';
import JsonData from './data/data.json';
import EngJsonData from './data/engdata.json';

export class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      landingPageData: {},
      english: false,
    };

    // This binding is necessary to make `this` work in the callback
    this.handleClick = this.handleClick.bind(this);
  }

  // state = {
  //   landingPageData: {},
  // }

  getlandingPageData() {
    this.setState({landingPageData : JsonData})
  }

  componentDidMount() {
    this.getlandingPageData();
  }

  handleClick() {
    this.setState(state => ({
      english: !state.english
    }));
  }

  Nav() {
    return (
      <nav id="menu" className="navbar navbar-default navbar-fixed-top">
        <div className="container">
          <div className="navbar-header">
            <button
              type="button"
              className="navbar-toggle collapsed"
              data-toggle="collapse"
              data-target="#bs-example-navbar-collapse-1"
            >
              {" "}
              <span className="sr-only">Toggle navigation</span>{" "}
              <span className="icon-bar"></span>{" "}
              <span className="icon-bar"></span>{" "}
              <span className="icon-bar"></span>{" "}
            </button>
            <a className="navbar-brand page-scroll" href="#page-top">
              福石自動車（有）
            </a>{" "}
          </div>

          <div
            className="collapse navbar-collapse"
            id="bs-example-navbar-collapse-1"
          >
            <ul className="nav navbar-nav navbar-right">
              <li>
                <a href="#features" className="page-scroll">
                  Featuresssss
                </a>
              </li>
              <li>
                <a href="#about" className="page-scroll">
                  About
                </a>
              </li>
              <li>
                <a href="#services" className="page-scroll">
                  Services
                </a>
              </li>
              <li>
                <a href="#portfolio" className="page-scroll">
                  Gallery
                </a>
              </li>
              <li>
                <a href="#testimonials" className="page-scroll">
                  Testimonials
                </a>
              </li>
              {/* <li>
                <a href="#team" className="page-scroll">
                  Team
                </a>
              </li> */}
              <li>
                <a href="#contact" className="page-scroll">
                  Contact
                </a>
              </li>
              <a　className="btn btn-custom btn-lg page-scroll"　scroll-behavior= "smooth"　onClick={this.handleClick()}>
        {this.state.english ? 'Japanese' : 'English'} 
        </a>{" "}   
            </ul>
          </div>
        </div>
      </nav>
    );
  }

  render() {
    if (this.state.english) {
      return (
        <div>
        <this.Nav />
        <br />
        <br />
        <br />
        <br />
        <br />
        <a　className="btn btn-custom btn-lg page-scroll"　scroll-behavior= "smooth"　onClick={this.handleClick}>
        {this.state.english ? 'Japanese' : 'English'} 
        </a>{" "}     
        <Header data={EngJsonData.Header} />
        <Features data={EngJsonData.Features} />
        <About data={EngJsonData.About} />
        <Services data={EngJsonData.Services} />
        <Gallery />
        <Testimonials data={EngJsonData.Testimonials} />
        {/* <Team data={EngJsonData.Team} /> */}
        <Contact data={EngJsonData.Contact} />
        </div>
      )
    }
    else {
    return (
      <div>
        <Navigation />
        <br />
        <br />
        <br />
        <br />
        <br />
        <a　className="btn btn-custom btn-lg page-scroll"　scroll-behavior= "smooth"　onClick={this.handleClick}>
        {this.state.english ? 'Japanese' : 'English'} 
        </a>{" "}
        <Header data={this.state.landingPageData.Header} />
        <Features data={this.state.landingPageData.Features} />
        <About data={this.state.landingPageData.About} />
        <Services data={this.state.landingPageData.Services} />
        <Gallery />
        <Testimonials data={this.state.landingPageData.Testimonials} />
        {/* <Team data={this.state.landingPageData.Team} /> */}
        <Contact data={this.state.landingPageData.Contact} />
      </div>
    )
    }
  }
}

export default App;
