import React, { Component } from 'react'
import Navigation from './components/navigation';
import Header from './components/header';
import Features from './components/features';
import About from './components/about';
import Services from './components/services';
import Gallery from './components/gallery';
import Testimonials from './components/testimonials';
import Team from './components/Team';
import Contact from './components/contact';
import JsonData from './data/data.json';
import EngJsonData from './data/engdata.json';

export class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      landingPageData: {},
      english: false,
    };

    // This binding is necessary to make `this` work in the callback
    this.handleClick = this.handleClick.bind(this);
  }

  // state = {
  //   landingPageData: {},
  // }

  getlandingPageData() {
    this.setState({landingPageData : JsonData})
  }

  componentDidMount() {
    this.getlandingPageData();
  }

  handleClick() {
    this.setState(state => ({
      english: !state.english
    }));
  }

  render() {
    if (this.state.english) {
      return (
        <div>
        <Navigation />
        <Header data={EngJsonData.Header} />
        <button onClick={this.handleClick}> {this.state.english ? 'ENGLISH' : 'JAP'} </button> 
        <Features data={EngJsonData.Features} />
        <About data={EngJsonData.About} />
        <Services data={EngJsonData.Services} />
        <Gallery />
        <Testimonials data={EngJsonData.Testimonials} />
        {/* <Team data={EngJsonData.Team} /> */}
        <Contact data={EngJsonData.Contact} />
        </div>
      )
    }
    else {
    return (
      <div>
        <Navigation />
        <Header data={this.state.landingPageData.Header} />
        <button onClick={this.handleClick}> {this.state.english ? 'ENGLISH' : 'JAP'} </button> 
        <Features data={this.state.landingPageData.Features} />
        <About data={this.state.landingPageData.About} />
        <Services data={this.state.landingPageData.Services} />
        <Gallery />
        <Testimonials data={this.state.landingPageData.Testimonials} />
        {/* <Team data={this.state.landingPageData.Team} /> */}
        <Contact data={this.state.landingPageData.Contact} />
      </div>
    )
    }
  }
}

export default App;
